func stringSlicer(input string) []string {
	return strings.Split(input, "\n")
}

func SumOfCalibrationModels(input []string) (sum int) {
	var firstDigit int
	var secondDigit int
	var err error
	for line := range input {
		for i := 0; i < len(input[line]); i++ {
			Digit1 := input[line][i]
			firstDigit, err = strconv.Atoi(string(Digit1))
			if err != nil {
				continue
			}

			break
		}
		for i := len(input[line]) - 1; i >= 0; i-- {
			Digit2 := input[line][i]
			secondDigit, err = strconv.Atoi(string(Digit2))
			if err != nil {
				continue
			}
			break
		}
		// number, _ := strconv.Atoi(strconv.Itoa(firstDigit) + strconv.Itoa(secondDigit)) Previous logic
		
		number := firstDigit*10 + secondDigit // Easier logic
		sum += number
	}

	return
}
